pragma solidity 0.8.17;

import { Address } from "@openzeppelin/contracts/utils/Address.sol";

import { PPM_RESOLUTION } from "./Constants.sol";
import { Token } from "../token/Token.sol";
import { IMasterVault } from "../vaults/interfaces/IMasterVault.sol";
import { TokenLibrary } from "../token/TokenLibrary.sol";

error AccessDenied();
error AlreadyExists();
error DoesNotExist();
error InvalidAddress();
error InvalidExternalAddress();
error InvalidFee();
error InvalidPoolCollection();
error InvalidToken();
error InvalidParam();
error NotEmpty();
error NotPayable();
error ZeroValue();
error NativeTokenAmountMismatch();
error IdenticalAddresses();

/**
 * @dev common utilities
 */
abstract contract Utils {
    using TokenLibrary for Token;
    using Address for address payable;

    // allows execution by the caller only
    modifier only(address caller) {
        _only(caller);

        _;
    }

    function _only(address caller) internal view {
        if (msg.sender != caller) {
            revert AccessDenied();
        }
    }

    // verifies that a value is greater than zero
    modifier greaterThanZero(uint256 value) {
        _greaterThanZero(value);

        _;
    }

    // error message binary size optimization
    function _greaterThanZero(uint256 value) internal pure {
        if (value == 0) {
            revert ZeroValue();
        }
    }

    // validates an address - currently only checks that it isn't null
    modifier validAddress(address addr) {
        _validAddress(addr);

        _;
    }

    // error message binary size optimization
    function _validAddress(address addr) internal pure {
        if (addr == address(0)) {
            revert InvalidAddress();
        }
    }

    // validates an external address - currently only checks that it isn't null or this
    modifier validExternalAddress(address addr) {
        _validExternalAddress(addr);

        _;
    }

    // error message binary size optimization
    function _validExternalAddress(address addr) internal view {
        if (addr == address(0) || addr == address(this)) {
            revert InvalidExternalAddress();
        }
    }

    // ensures that the fee is valid
    modifier validFee(uint32 fee) {
        _validFee(fee);

        _;
    }

    // error message binary size optimization
    function _validFee(uint32 fee) internal pure {
        if (fee > PPM_RESOLUTION) {
            revert InvalidFee();
        }
    }

    function _validateInputTokens(Token token0, Token token1) internal pure {
        _validAddress(address(token0));
        _validAddress(address(token1));

        if (token0 == token1) {
            revert IdenticalAddresses();
        }
    }

    /**
     * @dev deposits tokens to the master vault and verifies that msg.value corresponds to its type
     */
    function _depositToMasterVault(
        address masterVault,
        Token token,
        address provider,
        uint256 amount
    ) internal {
        if (token.isNative()) {
            if (msg.value < amount) {
                revert NativeTokenAmountMismatch();
            }

            // using a regular transfer here would revert due to exceeding the 2300 gas limit which is why we're using
            // call instead (via sendValue), which the 2300 gas limit does not apply for
            payable(address(masterVault)).sendValue(amount);

            // refund the provider for the remaining native token amount
            if (msg.value > amount) {
                payable(address(provider)).sendValue(msg.value - amount);
            }
        } else {
            if (msg.value > 0) {
                revert NativeTokenAmountMismatch();
            }

            token.safeTransferFrom(provider, address(masterVault), amount);
        }
    }
}
